function Modal(props){
  return (
      <div className="modal"  style={{background : props.color}}>
        <h4>{props.title[props.modalTitle]}</h4>
        <p>날짜</p>
        <p>상세내용</p>
        <button onClick = {props.modifyFnc}>글수정</button>
      </div>
  )
}

